themes/customizer/header-options/header-logo.php\";i:31;s:69:\"acmethemes/customizer/header-options/header-media-display-options.php\";i:32;s:52:\"acmethemes/customizer/header-options/header-news.php\";i:33;s:53:\"acmethemes/customizer/header-options/header-panel.php\";i:34;s:52:\"acmethemes/customizer/header-options/menu-option.php\";i:35;s:64:\"acmethemes/customizer/header-options/site-identity-placement.php\";i:36;s:55:\"acmethemes/customizer/header-options/social-options.php\";i:37;s:55:\"acmethemes/customizer/footer-section/footer-section.php\";i:38;s:58:\"acmethemes/customizer/feature-section/feature-category.php\";i:39;s:56:\"acmethemes/customizer/feature-section/feature-enable.php\";i:40;s:55:\"acmethemes/customizer/feature-section/feature-panel.php\";i:41;s:63:\"acmethemes/customizer/design-options/archive-sidebar-layout.php\";i:42;s:57:\"acmethemes/customizer/design-options/background-image.php\";i:43;s:52:\"acmethemes/customizer/design-options/blog-layout.php\";i:44;s:52:\"acmethemes/customizer/design-options/colors-cats.php\";i:45;s:55:\"acmethemes/customizer/design-options/colors-options.php\";i:46;s:51:\"acmethemes/customizer/design-options/custom-css.php\";i:47;s:53:\"acmethemes/customizer/design-options/design-panel.php\";i:48;s:66:\"acmethemes/customizer/design-options/front-page-sidebar-layout.php\";i:49;s:55:\"acmethemes/customizer/design-options/sidebar-layout.php\";i:50;s:55:\"acmethemes/customizer/design-options/sticky-sidebar.php\";}s:7:\"max_sel\";N;s:7:\"imports\";a:2:{s:5:\"child\";a:0:{}s:5:\"parnt\";a:0:{}}s:13:\"child_version\";s:5:\"2.0.1\";s:12:\"child_author\";s:10:\"acmethemes\";s:10:\"child_name\";s:14:\"SuperMag Child\";s:14:\"child_themeuri\";s:43:\"https://www.acmethemes.com/themes/supermag/\";s:15:\"child_authoruri\";s:27:\"https://www.acmethemes.com/\";s:11:\"child_descr\";s:990:\"Acme Themes ( https://www.acmethemes.com ) proudly presents SuperMag, a Ultimate Theme for Magazine. SuperMag is specially designed for news, magazin